import { evaluateJSExpression } from '../../../utils/evaluateJSExpression.js'

export const bindIfDirective = (element, componentContext) => {
    const value = element.getAttribute('data-flow-attribute-original-content-if')

    $VindEngine.queueReactiveEffect({
        effect: () => {
            const display = Boolean(evaluateJSExpression(componentContext, value))

            element.style.display = display ? 'initial' : 'none'
        }
    })
}