export function extractLoopDescriptors(input) {
    const regex = /([\s\S]*?)\s+(?:in|of)\s+([\s\S]*)/;
    const match = input.match(regex);

    if (match) {
        return {
            itemName: match[1],
            arrayExpression: match[2].trim(),
        };
    } else {
        return null;
    }
}